SELECT c.customer_id customerId, c.store_id storeId, CONCAT(c.first_name,' ',c.last_name) name, c.email email, CASE WHEN c.active = 1 THEN '활동' ELSE '휴면' END active FROM customer c
AND c.store_id = #{storeId}
AND CONCAT(c.first_name,' ',c.last_name) LIKE CONCAT('%', #{searchWord}, '%')
LIMIT #{page.beginRow}, #{page.rowPerPage}

SELECT COUNT(*) FROM customer c
AND store_id = #{storeId}
AND CONCAT(c.first_name,' ',c.last_name) LIKE CONCAT('%', #{searchWord}, '%')
