mapper.select:
SELECT 
	COUNT(*)
FROM
	nicer_but_slower_film_list
    
    	
			AND category = #{category}
		
		
			AND rating = #{rating}
		
		
			AND price =  #{rentalRate}
		
		
			AND title LIKE CONCAT('%',#{searchWord},'%')
		
		
			AND actors LIKE CONCAT('%',#{searchActor},'%')

mapper.insert:
INSERT INTO film_actor(
	actor_id,
	film_id
) VALUES (
	#{actorId},
	#{filmId}
)
